Ext.define('Entregas100Web.view.CrearZonaWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.crearzonawindow',

    onBtnCancelarClick: function(button, e, eOpts) {
        this.view.close();
    },

    onBtnGuardarClick: function(button, e, eOpts) {
        var me = this,
            crearZonaForm = me.view.down("form").getForm(),
            nombrePlaza = me.view.down("#cmbPlaza").getDisplayValue(),
            zonasPanel = Ext.ComponentManager.get("zonasPanel"),
            zonasLocalStore = zonasPanel.getController().getStore("ZonasLocalStore"),
            record, waitWindow;

        if (crearZonaForm.isValid()) {
            waitWindow = Ext.MessageBox.wait("Agregando zona...");
            record = Ext.create(
            "Entregas100Web.model.ZonaModel",
            Ext.apply(crearZonaForm.getFieldValues(), {nombre_plaza: nombrePlaza})
            );
            zonasLocalStore.add(record);
            zonasLocalStore.sync({
                success: onSyncSuccess
            });
        }

        function onSyncSuccess() {
            waitWindow.close();
            me.view.close();
        }
    }

});
