Ext.define('Entregas100Web.view.EditarOperadorWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.editaroperadorwindow',

    onBtnCancelarClick: function(button, e, eOpts) {
        this.view.close();
    },

    onBtnGuardarClick: function(button, e, eOpts) {
        var me = this,
            editarOperadorForm = me.view.down("form").getForm(),
            nombrePlaza = me.view.down("#cmbPlaza").getDisplayValue(),
            tipoUsuario = me.view.down("#cmbTipoUsuario").getDisplayValue(),
            operadoresPanel = Ext.ComponentManager.get("operadoresPanel"),
            operadoresLocalStore = operadoresPanel.getController().getStore("OperadoresLocalStore"),
            record, waitWindow;

        if (editarOperadorForm.isValid()) {
            waitWindow = Ext.MessageBox.wait("Guardando cambios...");
            editarOperadorForm.updateRecord();
            record = editarOperadorForm.getRecord();

            // si el record no ha sufrigo cambios se termina la edicion
            if (!record.isDirty()) {
                waitWindow.close();
                me.view.close();
                return;
            }

            record.set("tipo_usuario", tipoUsuario);
            record.set("nombre_plaza", nombrePlaza);
            operadoresLocalStore.sync({
                success: onSyncSuccess
            });
        }

        function onSyncSuccess() {
            waitWindow.close();
            me.view.close();
        }
    },

    onEditarOperadorWindowBeforeRender: function(component, eOpts) {
        this.getStore("PlazasLocalStore").load();
    }

});
