Ext.define('Entregas100Web.view.EditarPerfilPagoWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.editarperfilpagowindow',

    onBtnCancelarClick: function(button, e, eOpts) {
        this.view.close();
    },

    onBtnGuardarClick: function(button, e, eOpts) {
        var me = this,
            editarPerfilPagoForm = me.view.down("form").getForm(),
            perfilesPagoPanel = Ext.ComponentManager.get("perfilesPagoPanel"),
            perfilesPagoLocalStore = perfilesPagoPanel.getController().getStore("PerfilesPagoLocalStore"),
            record, waitWindow;

        if (editarPerfilPagoForm.isValid()) {
            waitWindow = Ext.MessageBox.wait("Guardando cambios...");
            editarPerfilPagoForm.updateRecord();
            record = editarPerfilPagoForm.getRecord();

            // si el record no ha sufrigo cambios se termina la edicion
            if (!record.isDirty()) {
                waitWindow.close();
                me.view.close();
                return;
            }

            perfilesPagoLocalStore.sync({
                success: onSyncSuccess
            });
        }

        function onSyncSuccess() {
            waitWindow.close();
            me.view.close();
        }
    }

});
